<script>
import 'csshake/dist/csshake.min.css'
import { mapState, mapMutations } from 'vuex'
import { gsap } from 'gsap'
export default {
  data() {
    return {
      objectHasChosen: false
    }
  },
  computed: {
    ...mapState([
      'objectChosen',
      'isInteractLocked'
    ])
  },
  methods: {
    ...mapMutations([
      'updateObjectChosen',
      'updateCurrentSubview',
      'updateIsInteractLocked'
    ]),
    chooseOptionAsObject(role, e) {

      if (this.isInteractLocked === true) return

      this.updateIsInteractLocked({ value: true })
      
      this.updateObjectChosen({ value: role })
      this.objectHasChosen = true
      const elRoleFilled = e.target
      // reset unfilled roles
      gsap.set(
        [...this.$el.querySelectorAll('.chooseObject__cell--option')]
        .filter(element => element.children[0] !== elRoleFilled),
        {
          autoAlpha: .65,
        }
      )
      // reset filled roles
      gsap.to(
        [...this.$el.querySelectorAll('.chooseObject__cell--option > span')]
        .filter(element => element !== elRoleFilled),
        {
          opacity: 0,
          scale: .75
        }
      )
      // set chosen filled role
      gsap.to(
        elRoleFilled,
        {
          onStart: () => {
            gsap.set([elRoleFilled, elRoleFilled.parentNode] , {
              autoAlpha: 1
            })
          },
          duration: .75,
          scale: 1.45,
          ease: "elastic.out(1, 0.3)",
          onComplete: () => {
            this.updateIsInteractLocked({ value: false })
          }
        }
      )
    },
    confirmObject() {
      this.updateCurrentSubview({ value: 'GetStick' })
    }
  }
}
</script>

