<script>
import 'lazysizes'
import { gsap } from 'gsap'
import { 
  mapState, 
  mapActions
} from 'vuex'
export default {
  data() {
    return {
      shakeStarted: false,
      shakeEnded: false
    }
  },
  computed: {
    ...mapState(['objectChosen'])
  },
  methods: {
    ...mapActions(['getStick']),
    shake() {
      if (this.shakeStarted) return

      this.shakeStarted = true
      this.animateBackground()

      // const numberInDecisecond = Math.floor(Math.random() * 25) + 30
      // setTimeout(() => {
      //   this.getStick()
      // }, numberInDecisecond * 100)

    },
    stopShake() {
      this.getStick()
      this.animateBackgroundReset()
    },
    animateBackgroundReset() {
      gsap.killTweensOf(this.$refs.beam.$el)
      gsap.killTweensOf(this.$refs.halo.$el)
      gsap.killTweensOf(this.$refs.shaking)

      gsap.set([
        this.$refs.beam.$el,
        this.$refs.halo.$el
      ], {
        clearProps: 'all',
        onComplete: () => {
          this.$refs.beam.animate()
          this.$refs.halo.animate()
        }
      })

      gsap.to(
        [
          this.$refs.shaking, 
          this.$refs.pull
        ],
        {
          autoAlpha: 0,
          scale: .95,
          duration: .35,
          ease: "expo.in"
        }
      )

      this.shakeEnded = true

    },
    animateBackground() {
      gsap.killTweensOf(this.$refs.beam.$el)
      gsap.killTweensOf(this.$refs.halo.$el)

      const elsBackground = [
        this.$refs.beam.$el,
        this.$refs.halo.$el,
        this.$refs.pull
      ]
      const timelineBackground = gsap.timeline({
        repeat: -1,
        repeatDelay: 1.75
      })

      const timelineScalePulse = gsap
        .timeline({
          repeat: 1
        })
        .add(gsap.from(
          elsBackground,
          {
            scale: .75,
            duration: .35
          }
        ))
        .add(gsap.to(
          elsBackground,
          {
            scale: .75,
            duration: .275
          }
        ))

      timelineBackground
        .add(timelineScalePulse)
        .timeScale(1.5)
    }

  }
}
</script>

